import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

// Components
import Navigation from './components/Navigation';
import Home from './components/Home';
import VerifyProduct from './components/VerifyProduct';
import AddProduct from './components/AddProduct';
import GetContract from './components/GetContract';
import DeployContract from './components/DeployContract';

// ABIs
import CentralABI from './abis/Cental_ABI.json';

// Config
import config from './config.json';

function App() {
  const [provider, setProvider] = useState(null);
  const [central, setCentral] = useState(null);
  const [account, setAccount] = useState(null);
  const [loading, setLoading] = useState(true);

  function showErrorMessage(error) {
    alert(`An error occurred while connecting to MetaMask: ${error.message}\nCheck if you have MetaMask wallet installed`);
  }

  const loadBlockchainData = async () => {
    try {
      if (window.ethereum) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = await provider.getSigner();
        setProvider(provider);
        const network = await provider.getNetwork();

        const centralContractAddress = config[network.chainId].central.address;
        const central = new ethers.Contract(centralContractAddress, CentralABI, signer);
        setCentral(central);

        const accounts = await provider.listAccounts();
        setAccount(accounts[0]); // Assuming you want to set the first account

        setLoading(false); // Set loading to false after successfully fetching data
      } else {
        showErrorMessage(new Error('MetaMask not found. Please install MetaMask.'));
        setLoading(false); // Set loading to false in case of MetaMask not found
      }
    } catch (error) {
      console.error(error);
      showErrorMessage(error);
      setLoading(false); // Set loading to false in case of an error
    }
  };

  useEffect(() => {
    loadBlockchainData();

    // Listen for provider changes (account changes, network changes)
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', () => {
        // Handle account changes
        loadBlockchainData();
      });

      window.ethereum.on('chainChanged', () => {
        // Handle network changes
        loadBlockchainData();
      });
    }

    // Cleanup the event listeners when the component unmounts
    return () => {
      if (window.ethereum) {
        window.ethereum.removeAllListeners('accountsChanged');
        window.ethereum.removeAllListeners('chainChanged');
      }
    };
  }, []);

  return (
    <Router>
      <Navigation account={account} provider={provider} central={central} setAccount={setAccount} />
      {!loading ? (
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/createcontract" element={<DeployContract account={account} provider={provider} central={central} />} />
          <Route path="/getcontract" element={<GetContract account={account} provider={provider} central={central} />} />
          <Route path="/addproduct" element={<AddProduct account={account} provider={provider} central={central} />} />
          <Route path="/verify" element={<VerifyProduct account={account} provider={provider} central={central} />} />
        </Routes>
      ) : (
        <div>Loading...</div>
      )}
    </Router>
  );
}

export default App;
